exports.trim = function(str, spliter){
	var regexp;
	if(!spliter){
		regexp = /^(\s|\u00A0)+|(\s|\u00A0)+$/g;
	}else{
		regexp = new RegExp("^(" + spliter + ")+|(" + spliter + ")+$", "g");
	}
	
	return str.replace(regexp, '');
};


exports.dashToUnderScore = function(str){
	return str.replace(/-/g, '_');
};

exports.dashToCamel = function(str){
	return str.replace(/-([a-z])/, function(matches){
		return matches[1].toUpperCase();
	});
}
